import React, { useContext, useState } from "react";
import Layout from "./../components/Layout/Layout";
import { useCart } from "../context/cart";
import { useAuth } from "../context/auth";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/CartStyles.css";
import { AppContext } from "../context/AppContext";  // Make sure this context exists and is being used properly

const CartPage = () => {
  const [auth] = useAuth();
  const [cart, setCart] = useCart();
  const navigate = useNavigate();
  const { userAddress, url, user, clearCart } = useContext(AppContext); // Use AppContext for user, url, userAddress, etc.

  // State variables for quantity and price
  const [qty, setQty] = useState(1); // Setting a default quantity to avoid undefined values
  const [price, setPrice] = useState(0);

  // Calculate total price of items in the cart
  const totalPrice = () => {
    let total = 0;
    cart?.forEach((item) => {
      total += item.price || 0; // Handle cases where price is undefined
    });
    return total.toLocaleString("en-IN", {
      style: "currency",
      currency: "INR",
    });
  };

  // Remove an item from the cart
  const removeCartItem = (productId) => {
    const updatedCart = cart.filter((item) => item._id !== productId);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart)); // Store updated cart in localStorage
  };

  // Handle payment through Razorpay
  const handlePayment = async () => {
    try {
      // Ensure price is properly calculated before making the API call
      const orderResponse = await axios.post(`${url}/payment/checkout`, {
        amount: price,
        qty,
        cartItems: cart, // Pass the entire cart items to the backend
        userShipping: userAddress,
        userId: user?._id, // Access user information safely
      });

      console.log("order response", orderResponse);

      // Get order data from response
      const { orderId, amount: orderAmount } = orderResponse.data;

      var options = {
        key: "rzp_test_gHH711O4gcSjCq", // Replace with your Razorpay Key ID
        amount: orderAmount * 100, // Amount is in subunits (paise for INR)
        currency: "INR",
        name: "Web Dev Mastery",
        description: "Web Dev Mastery",

        order_id: orderId, // This is a sample Order ID. Pass the `id` obtained in the response of Step 1.
        handler: async function (response) {
          // Collect payment data
          const paymentData = {
            orderId: response.razorpay_order_id,
            paymentId: response.razorpay_payment_id,
            signature: response.razorpay_signature,
            amount: orderAmount,
            orderItems: cart, // Passing cart items to be verified
            userId: user._id,
            userShipping: userAddress,
          };

          // Send payment data to backend for verification
          const api = await axios.post(
            `${url}/payment/verify-payment`,
            paymentData
          );

          console.log("razorpay res", api.data);

          if (api.data.success) {
            // Clear the cart and redirect to order confirmation page on success
            clearCart();
            navigate("/orderconfirmation"); // Changed to correct path: /orderconfirmation
          } else {
            // Handle failed verification
            console.log("Payment verification failed:", api.data.message);
          }
        },
        prefill: {
          name: user?.name || "Web Dev Mastery", // Assuming user object exists
          email: user?.email || "webdevmastery@gmail.com",
          contact: user?.contact || "9000090000",
        },
        notes: {
          address: userAddress || "Vijay Nagar Indore", // Default address if not available
        },
        theme: {
          color: "#3399cc",
        },
      };

      // Initialize Razorpay payment gateway
      const rzp = new window.Razorpay(options);
      rzp.open();
    } catch (error) {
      console.error("Error during Razorpay payment initiation", error);
    }
  };

  return (
    <Layout>
      <div className="cart-page">
        <div className="row">
          <div className="col-md-12">
            <h1 className="text-center bg-light p-2 mb-1">
              {!auth?.user ? "Hello Guest" : `Hello ${auth?.user?.name}`}
              <p className="text-center">
                {cart?.length
                  ? `You have ${cart.length} items in your cart ${
                      auth?.token ? "" : ", please login to checkout!"
                    }`
                  : "Your Cart Is Empty"}
              </p>
            </h1>
          </div>
        </div>
        <div className="container">
          <div className="row">
            <div className="col-md-7 p-0 m-0">
              {cart?.map((item) => (
                <div className="row card flex-row mb-3" key={item._id}>
                  <div className="col-md-4">
                    <img
                      src={`/api/v1/product/product-photo/${item._id}`}
                      alt={item.name}
                      className="card-img-top"
                      height="150px"
                    />
                  </div>
                  <div className="col-md-5">
                    <h5>{item.name}</h5>
                    <p>{item.description?.substring(0, 50)}...</p>
                    <p>Price: {item.price}</p>
                  </div>
                  <div className="col-md-3">
                    <button
                      className="btn btn-danger"
                      onClick={() => removeCartItem(item._id)}
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>
            <div className="col-md-5 cart-summary">
              <h3>Cart Summary</h3>
              <p>Total | Checkout | Payment</p>
              <hr />
              <h4>Total: {totalPrice()}</h4>
              {auth?.user?.address ? (
                <div className="mb-3">
                  <h5>Shipping Address:</h5>
                  <p>{auth?.user?.address}</p>
                  <button
                    className="btn btn-outline-warning"
                    onClick={() => navigate("/dashboard/user/profile")}
                  >
                    Update Address
                  </button>
                </div>
              ) : auth?.token ? (
                <button
                  className="btn btn-outline-warning"
                  onClick={() => navigate("/dashboard/user/profile")}
                >
                  Add Address
                </button>
              ) : (
                <button
                  className="btn btn-outline-warning"
                  onClick={() =>
                    navigate("/login", {
                      state: { from: "/cart" },
                    })
                  }
                >
                  Login to Proceed
                </button>
              )}
              <div className="mt-2">
                {auth?.token && cart.length > 0 && auth.user.address && (
                  <button
                    className="btn btn-primary"
                    onClick={handlePayment}
                  >
                    Proceed to Pay
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default CartPage;
