import React, { useState, useContext } from "react";
import Layout from "./../components/Layout/Layout";
import { useCart } from "../context/cart";
import { useAuth } from "../context/auth";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/CartStyles.css";

// Define the CartPage component
const CartPage = () => {
  const [auth] = useAuth(); // Get authentication info from context
  const [cart, setCart] = useCart(); // Get cart state from context
  const navigate = useNavigate(); // Access navigate function from react-router
  const { userAddress, url, user, clearCart } = useContext(); // Context values

  const [qty, setQty] = useState(0); // Quantity state
  const [price, setPrice] = useState(0); // Price state

  // Function to calculate total price of items in the cart
  const totalPrice = () => {
    let total = 0;
    cart.forEach((item) => {
      total += item.price || 0; // Handle cases where price is undefined
    });
    return total.toLocaleString("en-IN", {
      style: "currency",
      currency: "INR",
    });
  };

  // Remove an item from the cart
  const removeCartItem = (productId) => {
    const updatedCart = cart.filter((item) => item._id !== productId);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  // Handle payment through Razorpay
  const handlePayment = async () => {
    try {
      // Sending the request to create order
      const orderResponse = await axios.post(`${url}/payment/checkout`, {
        amount: price,
        qty: qty,
        cartItems: cart?.items,
        userShipping: userAddress,
        userId: user._id,
      });

      console.log("Order response:", orderResponse);
      const { orderId, amount: orderAmount } = orderResponse.data;

      // Configure Razorpay options
      const options = {
        key: "rzp_test_OxAxRfdIt6ofSm", // Replace with your Razorpay Key ID
        amount: orderAmount * 100, // Amount in subunits (INR Paise, for example)
        currency: "INR",
        name: "Web Dev Mastery",
        description: "Web Dev Mastery", 
        order_id: orderId, // Pass the order ID here
        handler: async (response) => {
          // Handle the payment response
          const paymentData = {
            orderId: response.razorpay_order_id,
            paymentId: response.razorpay_payment_id,
            signature: response.razorpay_signature,
            amount: orderAmount,
            orderItems: cart?.items,
            userId: user._id,
            userShipping: userAddress,
          };

          try {
            // Verifying the payment with backend
            const api = await axios.post(
              `${url}/payment/verify-payment`,
              paymentData
            );
            console.log("Razorpay Response:", api.data);

            // On success, clear cart and navigate to confirmation page
            if (api.data.success) {
              clearCart();
              navigate("/orderconfirmation");
            }
          } catch (error) {
            console.log("Payment verification failed:", error);
          }
        },
        prefill: {
          name: "Web Dev Mastery", // Prefilled name
          email: "webdevmastery@gmail.com", // Prefilled email
          contact: "9000090000", // Prefilled phone number
        },
        notes: {
          address: "Vijay Nagar Indore", // Address as a note
        },
        theme: {
          color: "#3399cc", // Razorpay theme color
        },
      };

      // Open the Razorpay payment modal
      const rzp = new window.Razorpay(options);
      rzp.open();
    } catch (error) {
      console.log("Error while processing payment:", error);
    }
  };

  return (
    <Layout>
      <div className="cart-page">
        <div className="row">
          <div className="col-md-12">
            <h1 className="text-center bg-light p-2 mb-1">
              {!auth?.user ? "Hello Guest" : `Hello ${auth?.user?.name}`}
              <p className="text-center">
                {cart?.length
                  ? `You have ${cart.length} items in your cart ${
                      auth?.token ? "" : ", please login to checkout!"
                    }`
                  : "Your Cart Is Empty"}
              </p>
            </h1>
          </div>
        </div>
        <div className="container">
          <div className="row">
            <div className="col-md-7 p-0 m-0">
              {cart?.map((item) => (
                <div className="row card flex-row mb-3" key={item._id}>
                  <div className="col-md-4">
                    <img
                      src={`/api/v1/product/product-photo/${item._id}`}
                      alt={item.name}
                      className="card-img-top"
                      height="150px"
                    />
                  </div>
                  <div className="col-md-5">
                    <h5>{item.name}</h5>
                    <p>{item.description?.substring(0, 50)}...</p>
                    <p>Price: {item.price}</p>
                  </div>
                  <div className="col-md-3">
                    <button
                      className="btn btn-danger"
                      onClick={() => removeCartItem(item._id)}
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>
            <div className="col-md-5 cart-summary">
              <h3>Cart Summary</h3>
              <p>Total | Checkout | Payment</p>
              <hr />
              <h4>Total: {totalPrice()}</h4>
              {auth?.user?.address ? (
                <div className="mb-3">
                  <h5>Shipping Address:</h5>
                  <p>{auth.user.address}</p>
                  <button
                    className="btn btn-outline-warning"
                    onClick={() => navigate("/dashboard/user/profile")}
                  >
                    Update Address
                  </button>
                </div>
              ) : auth?.token ? (
                <button
                  className="btn btn-outline-warning"
                  onClick={() => navigate("/dashboard/user/profile")}
                >
                  Add Address
                </button>
              ) : (
                <button
                  className="btn btn-outline-warning"
                  onClick={() =>
                    navigate("/login", {
                      state: { from: "/cart" },
                    })
                  }
                >
                  Login to Proceed
                </button>
              )}
              <div className="mt-2">
                {auth?.token && cart.length > 0 && auth.user.address && (
                  <button
                    className="btn btn-primary"
                    onClick={handlePayment}
                  >
                    Proceed to Pay
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default CartPage;
