// eslint-disable-next-line
import React, { useEffect } from "react";
import Layout from "./../components/Layout/Layout";
import { useCart } from "../context/cart";
import { useAuth } from "../context/auth";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/CartStyles.css";

const CartPage = () => {
  const [auth] = useAuth();
  const [cart, setCart] = useCart();
  const navigate = useNavigate();

  // Calculate total price of items in the cart
  const totalPrice = () => {
    return cart.reduce((total, item) => total + (item.price || 0), 0).toLocaleString("en-IN", {
      style: "currency",
      currency: "INR",
    });
  };

  // Remove an item from the cart
  const removeCartItem = (productId) => {
    const updatedCart = cart.filter((item) => item._id !== productId);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  // Handle Razorpay payment
  const handlePayment = async () => {
    try {
      const totalAmount = parseInt(totalPrice().replace(/[^\d]/g, "")); // Convert total to numeric value in paise

      // Create an order on the server
      const { data: orderResponse } = await axios.post("/api/v1/payment/create-order", {
        amount: totalAmount, // Amount in paise
      });

      const { id: orderId, amount } = orderResponse;

      // Razorpay payment options
      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID || "rzp_test_OxAxRfdIt6ofSm", // Use environment variable or fallback
        amount: amount*1, // Amount in paise
        currency: "INR",
        name: "Your Shop Name",
        description: "Order Payment",
        order_id: orderId,
        handler: async (response) => {
          try {
            // Payment verification
            const { razorpay_order_id, razorpay_payment_id, razorpay_signature } = response;

            const verificationData = {
              razorpay_order_id,
              razorpay_payment_id,
              razorpay_signature,
              cart,
            };

            const { data: verificationResponse } = await axios.post(
              "/api/v1/payment/verify-payment",
              verificationData
            );

            if (verificationResponse.ok) {
              // Clear the cart and navigate to order confirmation
              setCart([]);
              localStorage.removeItem("cart");
              navigate("/dashboard/user/orders");
            } else {
              console.error("Payment verification failed");
              alert("Payment verification failed. Please try again.");
            }
          } catch (error) {
            console.error("Error during payment verification:", error.message);
            alert("An error occurred during payment verification.");
          }
        },
        prefill: {
          name: auth?.user?.name || "Guest User",
          email: auth?.user?.email || "guest@example.com",
          contact: auth?.user?.phone || "9000000000",
        },
        theme: {
          color: "#3399cc",
        },
      };

      // Open Razorpay payment modal
      const razorpay = new window.Razorpay(options);
      razorpay.open();
    } catch (error) {
      console.error("Error initiating Razorpay payment:", error.message);
      alert("Error initiating payment. Please try again.");
    }
  };

  return (
    <Layout>
      <div className="cart-page">
        <div className="row">
          <div className="col-md-12">
            <h1 className="text-center bg-light p-2 mb-1">
              {!auth?.user ? "Hello Guest" : `Hello ${auth?.user?.name}`}
              <p className="text-center">
                {cart?.length
                  ? `You have ${cart.length} items in your cart ${
                      auth?.token ? "" : ", please login to checkout!"
                    }`
                  : "Your Cart Is Empty"}
              </p>
            </h1>
          </div>
        </div>
        <div className="container">
          <div className="row">
            <div className="col-md-7 p-0 m-0">
              {cart?.map((item) => (
                <div className="row card flex-row mb-3" key={item._id}>
                  <div className="col-md-4">
                    <img
                      src={`/api/v1/product/product-photo/${item._id}`}
                      alt={item.name}
                      className="card-img-top"
                      height="150px"
                    />
                  </div>
                  <div className="col-md-5">
                    <h5>{item.name}</h5>
                    <p>{item.description?.substring(0, 50)}...</p>
                    <p>Price: {item.price}</p>
                  </div>
                  <div className="col-md-3">
                    <button
                      className="btn btn-danger"
                      onClick={() => removeCartItem(item._id)}
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>
            <div className="col-md-5 cart-summary">
              <h3>Cart Summary</h3>
              <p>Total | Checkout | Payment</p>
              <hr />
              <h4>Total: {totalPrice()}</h4>
              {auth?.user?.address ? (
                <div className="mb-3">
                  <h5>Shipping Address:</h5>
                  <p>{auth.user.address}</p>
                  <button
                    className="btn btn-outline-warning"
                    onClick={() => navigate("/dashboard/user/profile")}
                  >
                    Update Address
                  </button>
                </div>
              ) : auth?.token ? (
                <button
                  className="btn btn-outline-warning"
                  onClick={() => navigate("/dashboard/user/profile")}
                >
                  Add Address
                </button>
              ) : (
                <button
                  className="btn btn-outline-warning"
                  onClick={() =>
                    navigate("/login", {
                      state: { from: "/cart" },
                    })
                  }
                >
                  Login to Proceed
                </button>
              )}
              <div className="mt-2">
                {auth?.token && cart.length > 0 && auth.user.address && (
                  <button className="btn btn-primary" onClick={handlePayment}>
                    Proceed to Pay
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default CartPage;
