import React, { useEffect, useState } from "react";
import axios from "axios";
import Layout from "../../components/Layout/Layout";
import UserMenu from "../../components/Layout/UserMenu"; // Import UserMenu
//import "../../styles/OrdersStyles.css";

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);

  // Fetch user orders from the server
  const fetchOrders = async () => {
    try {
      const { data } = await axios.get("/api/v1/auth/user-orders", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`, // Pass JWT token
        },
      });

      if (data.success) {
        setOrders(data.orders);
      } else {
        console.error("Failed to fetch orders:", data.message);
      }
    } catch (error) {
      console.error("Error fetching orders:", error.message);
    }
  };

  // Load orders on component mount
  useEffect(() => {
    fetchOrders();
  }, []);

  return (
    <Layout>
      <div className="container-fluid orders-page">
        <div className="row">
          <div className="col-md-3">
            <UserMenu /> {/* Add the UserMenu here */}
          </div>
          <div className="col-md-9">
            <h1 className="text-center">Your Orders</h1>
            {orders.length === 0 ? (
              <h3 className="text-center">No orders found</h3>
            ) : (
              <div className="container">
                <div className="row">
                  {orders.map((order) => (
                    <div className="col-md-12 mb-4" key={order._id}>
                      <div className="card">
                        <div className="card-header">
                          <h5>Order ID: {order._id}</h5>
                          <p>Order Date: {new Date(order.createdAt).toLocaleDateString()}</p>
                          <p>Payment ID: {order.payment.razorpay_payment_id}</p>
                          <p>Total Products: {order.products.length}</p>
                        </div>
                        <div className="card-body">
                          <div className="container">
                            {order.products.map((product) => (
                              <div
                                className="row mb-2 p-3 card flex-row"
                                key={product._id}
                              >
                                <div className="col-md-4">
                                  <img
                                    src={`/api/v1/product/product-photo/${product._id}`}
                                    className="card-img-top"
                                    alt={product.name}
                                    width="100px"
                                    height="100px"
                                  />
                                </div>
                                <div className="col-md-8">
                                  <p><strong>{product.name}</strong></p>
                                  <p>{product.description?.substring(0, 30)}...</p>
                                  <p>Price: â‚¹{product.price}</p>
                                </div>
                              </div>
                            ))}
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default OrdersPage;
