import React from "react";
import Layout from "./../components/Layout/Layout";
import { useCart } from "../context/cart";
import { useAuth } from "../context/auth";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import toast from "react-hot-toast";
import "../styles/CartStyles.css";

const CartPage = () => {
    const [auth] = useAuth();
    const [cart, setCart] = useCart();
    const navigate = useNavigate();

    const totalPrice = () => {
        try {
            let total = 0;
            cart.forEach((item) => {
                total += item.price;
            });
            return total.toLocaleString("en-IN", {
                style: "currency",
                currency: "INR",
            });
        } catch (error) {
            console.error("Error calculating total price:", error);
            return "₹0";
        }
    };

    const removeCartItem = (pid) => {
        try {
            const updatedCart = cart.filter((item) => item._id !== pid);
            setCart(updatedCart);
            localStorage.setItem("cart", JSON.stringify(updatedCart));
            toast.success("Item removed from cart!");
        } catch (error) {
            console.error("Error removing item from cart:", error);
        }
    };

    const handlePayment = async () => {
        try {
            if (!auth?.token || !auth?.user?.address) {
                toast.error("Please login and provide an address to checkout.");
                return;
            }

            const amount = parseInt(totalPrice().replace(/[^\d]/g, ""));
            if (isNaN(amount) || amount <= 0) {
              toast.error("Invalid amount. Please check your cart.");
              return;
            }
            const { data } = await axios.post("/api/payment/create-razorpay-order", { amount });

            const options = {
                key: process.env.REACT_APP_RAZORPAY_KEY_ID, // Use environment variable
                amount: data.amount,
                currency: "INR",
                name: "Thre60onwards",
                description: "Test Transaction",
                order_id: data.id,
                handler: async (response) => {
                    try {
                        const verifyData = await axios.post("/api/payment/verify-payment", {
                          ...response,
                          cart,
                          amount
                        });

                        if (verifyData.data.status === "success") {
                            setCart([]);
                            localStorage.removeItem("cart");
                            navigate("/dashboard/user/orders");
                            toast.success("Payment successful!");
                        } else {
                            toast.error(verifyData.data.message || "Payment verification failed.");
                        }
                    } catch (error) {
                        console.error("Error verifying payment:", error);
                        toast.error("Payment verification failed.");
                    }
                },
                prefill: {
                    name: auth?.user?.name || "",
                    email: auth?.user?.email || "",
                    contact: auth?.user?.phone || ""
                },
                theme: {
                    color: "#3399cc",
                },
            };

            const rzp1 = new window.Razorpay(options);
            rzp1.open();
        } catch (error) {
            console.error("Error initiating Razorpay:", error);
            toast.error("Failed to initiate payment.");
        }
    };

    return (
        <Layout>
            <div className="cart-page">
                <div className="row">
                    <div className="col-md-12">
                        <h1 className="text-center bg-light p-2 mb-1">
                            {auth?.user ? `Hello, ${auth.user.name}` : "Hello Guest"}
                            <p className="text-center">
                                {cart?.length
                                    ? `You have ${cart.length} items in your cart ${auth?.token ? "" : "please login to checkout!"
                                    }`
                                    : "Your cart is empty."}
                            </p>
                        </h1>
                    </div>
                </div>
                <div className="container">
                    <div className="row">
                        <div className="col-md-7 p-0 m-0">
                            {cart?.map((p) => (
                                <div className="row card flex-row" key={p._id}>
                                    <div className="col-md-4">
                                        <img
                                            src={`/api/product/product-photo/${p._id}`}
                                            className="card-img-top"
                                            alt={p.name}
                                            width="100%"
                                            height="130px"
                                            onError={(e) => {
                                                e.target.onerror = null;
                                                e.target.src = "/default-product-image.jpg";
                                            }}
                                        />
                                    </div>
                                    <div className="col-md-4">
                                        <p>{p.name}</p>
                                        <p>{p.description.substring(0, 30)}...</p>
                                        <p>Price: ₹{p.price}</p>
                                    </div>
                                    <div className="col-md-4 cart-remove-btn">
                                        <button
                                            className="btn btn-danger btn-sm btn-block"
                                            onClick={() => removeCartItem(p._id)}
                                        >
                                            Remove
                                        </button>
                                    </div>
                                </div>
                            ))}
                        </div>
                        <div className="col-md-5 cart-summary">
                            <h2>Cart Summary</h2>
                            <p>Total | Checkout | Payment</p>
                            <hr />
                            <h4>Total: {totalPrice()}</h4>
                            {auth?.user?.address ? (
                                <div className="mb-3">
                                    <h4>Current Address</h4>
                                    <h5>{auth.user.address}</h5>
                                    <button
                                        className="btn btn-outline-warning"
                                        onClick={() => navigate("/dashboard/user/profile")}
                                    >
                                        Update Address
                                    </button>
                                </div>
                            ) : (
                                <div className="mb-3">
                                    {auth?.token ? (
                                        <button
                                            className="btn btn-outline-warning"
                                            onClick={() => navigate("/dashboard/user/profile")}
                                        >
                                            Add Address
                                        </button>
                                    ) : (
                                        <button
                                            className="btn btn-outline-warning"
                                            onClick={() =>
                                                navigate("/login", {
                                                    state: "/cart",
                                                })
                                            }
                                        >
                                            Please Login to Checkout
                                        </button>
                                    )}
                                </div>
                            )}
                            <div className="mt-2">
                                {auth?.token && cart?.length ? (
                                    <button
                                        className="btn btn-primary"
                                        onClick={handlePayment}
                                        disabled={!auth?.user?.address}
                                    >
                                        Make Payment
                                    </button>
                                ) : null}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </Layout>
    );
};

export default CartPage;
