// eslint-disable-next-line
import React, { useEffect } from "react";
import Layout from "./../components/Layout/Layout";
import { useCart } from "../context/cart";
import { useAuth } from "../context/auth";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/CartStyles.css";

const CartPage = () => {
  const [auth] = useAuth();
  const [cart, setCart] = useCart();
  const navigate = useNavigate();

  // Calculate total price of items in the cart
  const totalPrice = () => {
    return cart.reduce((total, item) => total + (item.price || 0), 0).toLocaleString("en-IN", {
      style: "currency",
      currency: "INR",
    });
  };

  // Remove an item from the cart
  const removeCartItem = (productId) => {
    const updatedCart = cart.filter((item) => item._id !== productId);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  // Handle Razorpay payment integration
  const handlePayment = async () => {
    try {
      // Get the total price (amount) in paise (smallest currency unit)
      const totalAmount = parseInt(totalPrice().replace(/[^\d]/g, "")); // Remove non-numeric characters

      // Send request to create an order on the server
      const { data: orderResponse } = await axios.post("/api/v1/payment/create-", {
        amount: totalAmount,
        cartItems: cart,
        userShipping: auth?.user?.address,
        userId: auth?.user?._id,
      });

      // Extract order information from response
      const { orderId, amount } = orderResponse;

      if (!orderId || !amount) {
        throw new Error("Failed to create the order on the server");
      }

      // Razorpay options
      const options = {
        key: "rzp_test_OxAxRfdIt6ofSm", // Replace with your Razorpay Key ID
        amount: amount * 100, // Convert to currency subunits
        currency: "INR",
        name: "Your Shop Name",
        description: "Order Payment",
        order_id: orderId, // Pass the server-generated order ID
        handler: async function (response) {
          try {
            // Payment verification on the backend
            const verificationData = {
              orderId: response.razorpay_order_id,
              paymentId: response.razorpay_payment_id,
              signature: response.razorpay_signature,
            };

            const { data: verificationResponse } = await axios.post(
              "/api/v1/payment/verify-payment",
              verificationData
            );

            if (verificationResponse.success) {
              // Clear the cart and navigate to confirmation page on success
              setCart([]);
              localStorage.removeItem("cart");
              navigate("/orderconfirmation");
            } else {
              console.error("Payment verification failed");
            }
          } catch (error) {
            console.error("Error during payment verification:", error.message);
          }
        },
        prefill: {
          name: auth?.user?.name || "Guest User",
          email: auth?.user?.email || "guest@example.com",
          contact: auth?.user?.phone || "9000000000",
        },
        theme: {
          color: "#3399cc",
        },
      };

      // Initialize Razorpay instance and open the payment modal
      const razorpay = new window.Razorpay(options);
      razorpay.open();
    } catch (error) {
      console.error("Error initiating Razorpay payment:", error.message);
    }
  };

  return (
    <Layout>
      <div className="cart-page">
        <div className="row">
          <div className="col-md-12">
            <h1 className="text-center bg-light p-2 mb-1">
              {!auth?.user ? "Hello Guest" : `Hello ${auth?.user?.name}`}
              <p className="text-center">
                {cart?.length
                  ? `You have ${cart.length} items in your cart ${
                      auth?.token ? "" : ", please login to checkout!"
                    }`
                  : "Your Cart Is Empty"}
              </p>
            </h1>
          </div>
        </div>
        <div className="container">
          <div className="row">
            <div className="col-md-7 p-0 m-0">
              {cart?.map((item) => (
                <div className="row card flex-row mb-3" key={item._id}>
                  <div className="col-md-4">
                    <img
                      src={`/api/v1/product/product-photo/${item._id}`}
                      alt={item.name}
                      className="card-img-top"
                      height="150px"
                    />
                  </div>
                  <div className="col-md-5">
                    <h5>{item.name}</h5>
                    <p>{item.description?.substring(0, 50)}...</p>
                    <p>Price: {item.price}</p>
                  </div>
                  <div className="col-md-3">
                    <button
                      className="btn btn-danger"
                      onClick={() => removeCartItem(item._id)}
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>
            <div className="col-md-5 cart-summary">
              <h3>Cart Summary</h3>
              <p>Total | Checkout | Payment</p>
              <hr />
              <h4>Total: {totalPrice()}</h4>
              {auth?.user?.address ? (
                <div className="mb-3">
                  <h5>Shipping Address:</h5>
                  <p>{auth.user.address}</p>
                  <button
                    className="btn btn-outline-warning"
                    onClick={() => navigate("/dashboard/user/profile")}
                  >
                    Update Address
                  </button>
                </div>
              ) : auth?.token ? (
                <button
                  className="btn btn-outline-warning"
                  onClick={() => navigate("/dashboard/user/profile")}
                >
                  Add Address
                </button>
              ) : (
                <button
                  className="btn btn-outline-warning"
                  onClick={() =>
                    navigate("/login", {
                      state: { from: "/cart" },
                    })
                  }
                >
                  Login to Proceed
                </button>
              )}
              <div className="mt-2">
                {auth?.token && cart.length > 0 && auth.user.address && (
                  <button
                    className="btn btn-primary"
                    onClick={handlePayment}
                  >
                    Proceed to Pay
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default CartPage;
