import React from "react";
import Layout from "../components/Layout/Layout";
import { useCart } from "../context/cart";
import { useAuth } from "../context/auth";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import toast from "react-hot-toast"; // Ensure this is installed
import "../styles/CartStyles.css";

const CartPage = () => {
  const [auth] = useAuth();
  const [cart, setCart] = useCart();
  const navigate = useNavigate();

  // Calculate total price
  const totalPrice = () => {
    try {
      const total = cart.reduce((sum, item) => sum + item.price, 0);
      return total.toLocaleString("en-IN", {
        style: "currency",
        currency: "INR",
      });
    } catch (error) {
      console.error("Error calculating total price:", error);
      return "₹0"; // Fallback value
    }
  };

  // Remove item from cart
  const removeCartItem = (pid) => {
    try {
      const updatedCart = cart.filter((item) => item._id !== pid);
      setCart(updatedCart);
      localStorage.setItem("cart", JSON.stringify(updatedCart));
      toast.success("Item removed from cart!");
    } catch (error) {
      console.error("Error removing item from cart:", error);
    }
  };

  // Handle payment
  const handlePayment = async () => {
    try {
      const numericAmount = cart.reduce((sum, item) => sum + item.price, 0) * 100; // Razorpay expects the amount in paise
      const { data } = await axios.post("/api/v1/payment/create-razorpay-order", {
        amount: numericAmount, // Pass the amount in paise
      });

      const options = {
        key: "rzp_test_OxAxRfdIt6ofSm", // Replace with your Razorpay Key ID
        amount: data.amount,
        currency: "INR",
        name: "Thre60onwards",
        description: "Test Transaction",
        order_id: data.id,
        handler: async (response) => {
          try {
            const verifyData = await axios.post(
              "/api/v1/payment/razorpay-payment",
              response
            );

            if (verifyData.data.success) {
              // Create order in the database
              await axios.post(
                "/api/v1/auth/orders",
                {
                  products: cart,
                  payment: {
                    success: true,
                    order_id: data.id,
                    payment_id: response.razorpay_payment_id,
                  },
                },
                {
                  headers: {
                    Authorization: `Bearer ${auth.token}`,
                  },
                }
              );

              // Clear the cart
              setCart([]);
              localStorage.removeItem("cart");
              navigate("/dashboard/user/orders");
              toast.success("Payment successful!");
            }
          } catch (error) {
            console.error("Error verifying payment:", error);
            toast.error("Payment verification failed.");
          }
        },
        prefill: {
          name: auth?.user?.name || "",
          email: auth?.user?.email || "",
        },
        theme: {
          color: "#3399cc",
        },
      };

      const rzp = new window.Razorpay(options);
      rzp.open();
    } catch (error) {
      console.error("Error initializing Razorpay:", error);
      toast.error("Failed to initiate payment.");
    }
  };

  return (
    <Layout>
      <div className="cart-page">
        <div className="row">
          <div className="col-md-12">
            <h1 className="text-center bg-light p-2 mb-1">
              {auth?.user
                ? `Hello, ${auth.user.name}`
                : "Hello Guest"}
              <p className="text-center">
                {cart?.length
                  ? `You have ${cart.length} items in your cart ${
                      auth?.token ? "" : "please login to checkout!"
                    }`
                  : "Your cart is empty."}
              </p>
            </h1>
          </div>
        </div>
        <div className="container">
          <div className="row">
            <div className="col-md-7 p-0 m-0">
              {cart?.map((p) => (
                <div className="row card flex-row" key={p._id}>
                  <div className="col-md-4">
                    <img
                      src={`/api/v1/product/product-photo/${p._id}`}
                      className="card-img-top"
                      alt={p.name}
                      width="100%"
                      height="130px"

                    />
                  </div>
                  <div className="col-md-4">
                    <p>{p.name}</p>
                    <p>{p.description.substring(0, 30)}...</p>
                    <p>Price: ₹{p.price}</p>
                  </div>
                  <div className="col-md-4 cart-remove-btn">
                    <button
                      className="btn btn-danger btn-sm btn-block"
                      onClick={() => removeCartItem(p._id)}
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>
            <div className="col-md-5 cart-summary">
              <h2>Cart Summary</h2>
              <p>Total | Checkout | Payment</p>
              <hr />
              <h4>Total: {totalPrice()}</h4>
              {auth?.user?.address ? (
                <div className="mb-3">
                  <h4>Current Address</h4>
                  <h5>{auth.user.address}</h5>
                  <button
                    className="btn btn-outline-warning"
                    onClick={() => navigate("/dashboard/user/profile")}
                  >
                    Update Address
                  </button>
                </div>
              ) : (
                <div className="mb-3">
                  {auth?.token ? (
                    <button
                      className="btn btn-outline-warning"
                      onClick={() => navigate("/dashboard/user/profile")}
                    >
                      Add Address
                    </button>
                  ) : (
                    <button
                      className="btn btn-outline-warning"
                      onClick={() =>
                        navigate("/login", {
                          state: "/cart",
                        })
                      }
                    >
                      Please Login to Checkout
                    </button>
                  )}
                </div>
              )}
              <div className="mt-2">
                {auth?.token && cart?.length ? (
                  <button
                    className="btn btn-primary"
                    onClick={handlePayment}
                    disabled={!auth?.user?.address}
                  >
                    Make Payment
                  </button>
                ) : null}
              </div>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
}; 

export default CartPage;
